mapper.delete:

delete from player_commanders
where owner_id = #{ownerId,jdbcType=VARCHAR}
  and commander_id = #{commanderId,jdbcType=INTEGER}

mapper.update:

update player_commanders
set level = #{level,jdbcType=INTEGER},
  exp = #{exp,jdbcType=INTEGER},
  power = #{power,jdbcType=INTEGER},
  skill_info = #{skillInfo,jdbcType=VARCHAR,javaType=SkillInfo}
  talent_info = #{talentInfo,jdbcType=VARCHAR,javaType=TelentInfo},
  marching_info = #{marchingInfo,jdbcType=VARCHAR},
  hurt_times = #{hurtTimes,jdbcType=INTEGER},
  recover_time = #{recoverTime,jdbcType=TIMESTAMP}
where owner_id = #{ownerId,jdbcType=VARCHAR}
  and commander_id = #{commanderId,jdbcType=INTEGER}

mapper.select:

select owner_id, commander_id, level, exp, power, skill_info, talent_info, marching_info, 
hurt_times, recover_time
from player_commanders
where owner_id = #{ownerId,jdbcType=VARCHAR}
  and commander_id = #{commanderId,jdbcType=INTEGER}
